    public static void main(String[] args) {
        UserActivityManager userActivityManager = new UserActivityManager();

        System.out.println("Top 10 most active users:");
        userActivityManager.topTenMostActiveUsers(actions)
                .entrySet().forEach(entry -> System.out.println(entry.getKey() + " : " + entry.getValue()));

        System.out.println("\nTop 5 discussion topics");
        userActivityManager.topFiveDiscussionTopics(actions)
                .forEach(System.out::println);

        System.out.println("\nTop 3 users which left most comments:");
        userActivityManager.topThreeUsersLeftMostComments(actions)
                .entrySet().forEach(e -> System.out.println(e.getKey() + " " + e.getValue()));

        System.out.println("\nCalculate the percentage of actions:");
        userActivityManager.calculatePercentsOfActivity(actions)
                .entrySet().forEach(e -> System.out.println(e.getKey() + " " + e.getValue()));
    }
